tem{'fvtc',kMenuNoModifiers,currScript,0,0,noHierID,sysFont,naturalGlyph}, /* flip vartical */			skipItem{},			dataItem{'opaq',kMenuNoModifiers,currScript,0,0,noHierID,sysFont,naturalGlyph}, /* opaque */			dataItem{'trns',kMenuNoModifiers,currScript,0,0,noHierID,sysFont,naturalGlyph}, /* transparent */			dataItem{'blnd',kMenuNoModifiers,currScript,0,0,noHierID,sysFont,naturalGlyph}, /* blend */		}	}};resource 'xmnu' (139) /* icon */{	versionZero {		{			dataItem{'nico',kMenuShiftModifier,currScript,0,0,noHierID,sysFont,naturalGlyph}, /* new icon */			skipItem{},			skipItem{}, /* add icon */			skipItem{}, /* icon info */			skipItem{},			dataItem{'impi',kMenuShiftModifier,currScript,0,0,noHierID,sysFont,naturalGlyph}, /* import icon */			dataItem{'expi',kMenuShiftModifier,currScript,0,0,noHierID,sysFont,naturalGlyph}, /* export icon */		}	}};/* ダイアログのフォント *//* ダイアログのアピアランス */resource 'dlgx' (128,purgeable){	versionZero{kDialogFlagsUseThemeBackground+kDialogFlagsHandleMovableModal}};resource 'dlgx' (129,purgeable){	versionZero{kDialogFlagsUseThemeBackground+kDialogFlagsHandleMovableModal}};resource 'dlgx' (130,purgeable){	versionZero{kDialogFlagsUseThemeBackground+kDialogFlagsHandleMovableModal}};resource 'dlgx' (131,purgeable){	versionZero{kDialogFlagsUseThemeBackground+kDialogFlagsHandleMovableModal}};resource 'dlgx' (132,purgeable){	versionZero{kDialogFlagsUseThemeBackground+kDialogFlagsHandleMovableModal}};resource 'dlgx' (133,purgeable){	versionZero{kDialogFlagsUseThemeBackground+kDialogFlagsHandleMovableModal}};#if !TARGET_API_MAC_CARBONresource 'dlgx' (134,purgeable){	versionZero{kDialogFlagsUseThemeBackground+kDialogFlagsHandleMovableModal}};#endifresource 'dlgx' (135,purgeable){	versionZero{kDialogFlagsUseThemeBackground+kDialogFlagsHandleMovableModal}};resource 'dlgx' (136,purgeable){	versionZero{kDialogFlagsUseThemeBackground+kDialogFlagsHandleMovableModal}};resource 'dlgx' (137,purgeabl